import React, { useState } from "react";
import Slider from "react-slick";
const photos = ["university4.jpg", "university5.jpeg", "university3.jpg"];
const PhotoSlider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000,
    beforeChange: (current, next) => {
      setCurrentSlide(next);
    },
  };
  return (
    <div>
      <Slider {...settings}>
        {photos.map((photo, index) => (
          <div key={index} className="relative">
            <img
              src={`/images/${photo}`}
              alt={`Photo ${index + 1}`}
              className="img_slider w-full h-96 object-cover"
            />
          </div>
        ))}
      </Slider>
      <div className="absolute top-0 left-0 w-full h-96 flex items-center justify-center">
        <div className="bg-black bg-opacity-50 text-white px-4 py-2 rounded">
          <h1 className="text-xl">{photos[currentSlide]}</h1>
        </div>
      </div>
    </div>
  );
};

export default PhotoSlider;
