import React, { useState } from "react";
import Slider from "react-slick";
const photos = [
  { src: "university4.jpg", caption: "Get the Best Job Opportunities here" },
  {
    src: "university5.jpeg",
    caption: "Super Dream Offers to help you live your dream",
  },
  { src: "university3.jpg", caption: "Microsoft, Amazon and Other Companies" },
];
const PhotoSlider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000,
    beforeChange: (current, next) => {
      setCurrentSlide(next);
    },
  };
  return (
    <Slider {...settings}>
      {photos.map((photo, index) => (
        <div key={index} className="relative">
          <img
            src={`/images/${photo}`}
            alt={`Photo ${index + 1}`}
            className="img_slider w-full h-96 object-cover"
          />
          <div className="absolute top-0 left-0 w-full h-96 flex items-center justify-center">
            <div className="bg-black bg-opacity-50 text-white px-4 py-2 rounded">
              {currentSlide === index ? photo.caption : ""}
            </div>
          </div>
        </div>
      ))}
    </Slider>
  );
};

export default PhotoSlider;
